import { useState } from "react";

const Create = () => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState('');
    return (
        <div className="create mx-80 my-4">
            <h2 className="text-3xl font-semibold mb-4">Add a new blog</h2>
            <form className="flex flex-col my-8">
                <label htmlFor="title">Title: </label>
                <input value={title} onChange={(e) => { setTitle(e.target.value) }} required type="text" id="title" className="border border-black hover:border-red-500 focus:border-red-500 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " />
                <label htmlFor="body">Blog: </label>
                <textarea value={body} onChange={(e) => { setBody(e.target.value) }} required id="body" className="border border-black hover:border-red-500 focus:border-red-500 w-full h-20 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " />
                <label htmlFor="author">Author: </label>
                <select value={author} onChange={(e) => { setAuthor(e.target.value) }} required type="text" id="author" className="border border-black hover:border-red-500 focus:border-red-500 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " >
                    <option>Mario</option>
                    <option>Yoshi</option>
                </select>
                <button className="transition-all w-full border border-red-400 hover:bg-red-400 hover:text-white py-3 rounded-md mt-6">Add blog</button>
            </form>
        </div>
    );
}

export default Create;
