import { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const EditBlog = () => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState('Anonymous');
    const [isPending, setIsPending] = useState(false);
    const history = useHistory();
    const { id } = useParams();

    const [blog, isBlogPending, error] = useFetch(`http://localhost:8000/blogs/${id}`);

    useEffect(() => {
        if (blog) {
            setTitle(blog.title);
            setAuthor(blog.author);
            setBody(blog.body);
        }
    }, [blog])


    const handleSubmit = (e) => {
        e.preventDefault();
        const blog = { title, body, author };
        setIsPending(true);
        fetch(`http://localhost:8000/blogs/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(blog)
        }).then(() => {
            setIsPending(false);
            history.push(`/blogs/${id}`);
        })
    }

    return (
        <div className="create lg:mx-60 md:mx-40 mx-10 my-4 text-white">
            <h2 className="text-3xl font-semibold mb-4">Edit blog</h2>
            {error && <div>{error}</div>}
            {isBlogPending && <div>Loading...</div>}
            {blog && (
                <form className="flex flex-col my-8" onSubmit={handleSubmit}>
                    <label htmlFor="title">Title: </label>
                    <input value={title} onChange={(e) => { setTitle(e.target.value) }} required type="text" id="title" className="border border-black hover:border-purple-500 focus:border-purple-500 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-purple-500 focus:ring-1 rounded-md text-black " />
                    <label htmlFor="author">Author: </label>
                    <input value={author} onChange={(e) => { setAuthor(e.target.value) }} required type="text" id="author" className="border border-black hover:border-purple-500 focus:border-purple-500 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-purple-500 focus:ring-1 rounded-md text-black " />
                    <label htmlFor="body">Blog: </label>
                    <textarea value={body} onChange={(e) => { setBody(e.target.value) }} required id="body" className="border border-black hover:border-purple-500 focus:border-purple-500 w-full h-40 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-purple-500 focus:ring-1 rounded-md text-black " />
                    {!isPending && <button className="transition-all w-full border border-purple-400 hover:bg-purple-400 hover:text-white py-3 rounded-md mt-6">Update blog</button>}
                    {isPending && <button disabled className="transition-all w-full border border-purple-400 bg-purple-400 cursor-not-allowed text-white py-3 rounded-md mt-6">Updating blog...</button>}
                </form>
            )}
        </div>
    );
}

export default EditBlog;
