const Create = () => {
    return (
        <div className="create mx-80 my-4">
            <h2 className="text-3xl font-semibold mb-4">Add a new blog</h2>
            <form className="flex flex-col my-8">
                <label htmlFor="title">Title: </label>
                <input type="text" id="title" name="title" className="border border-gray-300 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " />
                <label htmlFor="body">Blog: </label>
                <textarea id="body" name="body" className="border border-gray-300 w-full h-20 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " />
                <label htmlFor="author">Author: </label>
                <input type="text" id="author" name="author" className="border border-gray-300 w-full h-5 px-3 py-5 mb-2 hover:outline-none focus:outline-none focus:ring-red-500 focus:ring-1 rounded-md text-black " />
                <button className="transition-all w-full border border-red-400 hover:bg-red-400 hover:text-white py-3 rounded-md mt-6">Add blog</button>
            </form>
        </div>
    );
}

export default Create;
