import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {

    const { id } = useParams();
    const [blog, isPending, error] = useFetch(`http://localhost:8000/blogs/${id}`);

    return (
        <div className="blog-details mx-8 my-4">

            {error && <div>{error}</div>}
            {isPending && <div>Loading...</div>}
            {blog && (
                <article>
                    <h2 className="text-3xl font-semibold mb-2">{blog.title}</h2>
                    <p className="text-md mb-6">Written by {blog.author}</p>
                    <div>
                        <p>{blog.body}</p>
                    </div>
                </article>
            )}
        </div>
    );
}

export default BlogDetails;
