import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {

    const { id } = useParams();
    const [blog, isPending, error] = useFetch(`localhost:8000/blogs/${id}`);

    return (
        <div className="blog-details mx-8 my-4">

            {error && <div>{error}</div>}
            {isPending && <div>Loading...</div>}
            {blog && <h2 className="text-3xl font-semibold mb-4">Blog details - {id}</h2>}
        </div>
    );
}

export default BlogDetails;
