import { Link, useHistory } from "react-router-dom";

const BlogList = ({ blogs, title }) => {
    const history = useHistory();
    const handleDelete = () => {
        fetch(`http://localhost:8000/blogs/${blog.id}`, {
            method: "DELETE"
        }).then(() => {
            history.push("/");
        });
    }
    return (
        <div className="blog-list mx-80 my-4">
            <h2 className="text-3xl font-semibold mb-4">{title}</h2>
            {blogs.map((blog) => (
                <div className="blog-preivew rounded-md transition-all px-4 py-2 hover:bg-gray-200 flex justify-between items-center" key={blog.id}>
                    <Link to={`/blogs/${blog.id}`}>
                        <h2 className="font-medium">{blog.title}</h2>
                        <p>Written by {blog.author}</p>
                    </Link >
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6 hover:text-red-500 cursor-pointer transition-all">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M15 12H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                </div>
            ))}
        </div>
    );
}

export default BlogList;
